<?php

namespace LouvreBundle\Repository;

/**
 * BilletsOptionRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class BilletsOptionRepository extends \Doctrine\ORM\EntityRepository
{

    /**
     * compte le nombre de billets mis en options pour uen date donnÃ©e
     *
     * @param date $date
     * @return int
     */
    public function compterOptions($date)
    {
       
        $date = new \dateTime($date);
 

        $result = $this
            ->createQueryBuilder('a')
            ->where('a.date = :date')
            ->setParameter('date', $date)
            ->getQuery()
            ->getResult();

        return count($result);
        //return $date;

    }

}
